import styled from "@emotion/styled";

export const TrasactionTable = styled.table`
width: 100%;
thead{
	background-color: #71c8f2;
	padding: 10px;
	display: block;
}

tbody {
	tr{
		text-align: center;
		td{
			font-size: 19px;
		}
	}
}

`;

export const Thead = styled.thead`

`
