import { getRandomColor } from "./rendomColor";

export function StatisticCard(props) {
	const {label, percent} = props;
	return (
	<Item color={`${changeColor()}`}>
			<Label >{label}</Label>
			<Percentage>{percent}</Percentage>
    </Item>
	)
};

