import { Transaction } from "components/Transation/Transaction"
import { TrasactionTable } from "./TransactionHistory.styled"
export function TransactionHistory({ items }) {

	let color = '';	

for (let i = 0; i < items.length; i+=1) {
	if (i % 2 !== 0) {
		 color = 'black';
		// console.log('black')
	} else {
		 color = 'white';
		// console.log('white');
	}
}

	return (
		<TrasactionTable>
  <thead>
    <tr>
      <th>Type</th>
      <th>Amount</th>
					<th>Currency</th>
    </tr>
  </thead>

			<tbody>
				{items.map(item => (
					<Transaction bacColor={color} key={item.id}
						type={item.type}
						amount={item.amount}
						currency={item.currency}
					/>

				))}


  </tbody>
	</TrasactionTable>
	)
}
