import styled from "@emotion/styled";

export const Item = styled.li`
width: 300px;
border: 2px solid gray;
border-radius: 4px;
display: flex;
align-items: center;
background-color: white;
padding-left: 20px;

	&:not(:last-child){
	margin-bottom: 10px;
	}
`;

export const Status = styled.span`
display: block;
margin-right: 20px;
width: 20px;
height: 20px;
background-color: red;
border-radius: 50%;

`
