import styled from "@emotion/styled";

export const ListStatistics = styled.section`
	width:350px;
	display: flex;
	flex-direction: column;
	align-items: center;
	text-align: center;


;`
export const HeadTitle = styled.h2`
	padding: 30px 0;
	background: white;
	border: 1px solid gray;
`;
export const StatList = styled.ul`
	display: flex;
`;
